import React, { Component } from 'react'
import './Announcement.css';
import {  Row, Col } from 'react-grid';
import Container from 'react-bootstrap/Container';
import 'bootstrap/dist/css/bootstrap.min.css';
import UserProfile from 'react-user-profile'

class Profile extends Component {
  render() {
    const photo = 'https://cdn.wallpapersafari.com/45/7/7ed5AD.jpg'
    const userName = 'Joan Adhiambo'
    const location = 'Nairobi,Kenya'

    const comments = [
      {
        id: '1',
        photo: 'https://cdn.wallpapersafari.com/45/7/7ed5AD.jpg',
        userName: 'Lenny Rono',
        content: 'Lorem ipsum dolor sit amet enim. Etiam ullamcorper. Suspendisse a pellentesque dui, non felis. Maecenas malesuada elit lectus felis, malesuada ultricies. Curabitur et ligula. ',
        createdAt: 22052022,
      }
    ]

    return (
      <Container>
        <Row>
      <Col>
      <div style={{ marginTop: '100px', width: '100%' }}>


            <UserProfile style={{ color}}photo={photo} userName={userName} location={location} initialLikesCount={121} initialFollowingCount={723} initialFollowersCount={4433} initialComments={comments} />
          </div>
      </Col>
        </Row>
      </Container>


    )
  }
}

export default Profile;
