import React, { useState, useEffect } from 'react';
import { Button } from './Button';
import { Link } from 'react-router-dom';
import './Navbar.css';
import {useSelector, useDispatch} from "react-redux"

function Navbar() {
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);

  const state=useSelector(state => state.auth)


  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  const authLink=(
    <span className="navbar-text ml-4">
      <strong>{state.user ? `welcome ${state.user.username}` : ''}</strong>
    </span>
  )


  const publicLink=(
    <ul className="navbar-nav ml-auto mt-2 mt-lg-0">
        <li className="nav-item">
        <Link to="/" className="nav-link active" aria-current="page">Register</Link>
        </li>
        <li className="nav-item">
        <Link to="/login" className="nav-link" href="#">Login</Link>
        </li> 
    </ul>
)


  useEffect(() => {
    showButton();
  }, []);

  window.addEventListener('resize', showButton);

  return (
    <>
      <nav className='navbar'>
        <div className='navbar-container'>
          <Link to='/' className='navbar-logo' onClick={closeMobileMenu}>
          <i class="fa fa-home"></i> &nbsp;
            Class Shedule
          </Link>
          <div className='menu-icon' onClick={handleClick}>
            <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
          </div>
          <ul className={click ? 'nav-menu active' : 'nav-menu'}>
            <li className='nav-item'>
              <Link to='/announcements' className='nav-links' onClick={closeMobileMenu}>
                Notifications
              </Link>
            </li>
            <li className='nav-item'>
              <Link to='/session' className='nav-links' onClick={closeMobileMenu}>
                Sessions
              </Link>
            </li>

            <li className='nav-item'>
              <Link
                to='/profile'
                className='nav-links'
                onClick={closeMobileMenu}
              >
                Profile
              </Link>
            </li>



            <li className='nav-item'>
              <Link
                to='/login'
                className='nav-links'
                onClick={closeMobileMenu}
              >
                Login
              </Link>
            </li>

            <li className='btn-signup'>
              <Link
                to='/signup'
                className='nav-links-mobile'
                onClick={closeMobileMenu}
              >
                Sign Up
              </Link>
            </li>
          </ul>
          {button && <Button className='signup' buttonStyle='btn--outline'>SIGN UP</Button>}

        </div>

                <div className="container-fluid">
                <a className="navbar-brand" href="#">Navbar</a>
                <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">

                <span className="navbar-toggler-icon"></span>
                </button>
                <div className="collapse navbar-collapse" id="navbarSupportedContent">
                     {state.isAuthenticated ? authLink : publicLink}
                </div>
                <span className="navbar-text ml-4">
      <strong>{state.user ? `welcome ${state.user.username}` : ''}</strong>
    </span>
            </div>
      </nav>
    </>
  );
}

export default Navbar;
