import React, { Component } from 'react';
import './Session.css'
import { GoogleLogin } from 'react-google-login'
import axios from 'axios';
import { useState } from 'react'

  var CLIENT_ID = "708105023641-t350k3usoo751tfnilu9hrdokjmmli8v.apps.googleusercontent.com"
  var API_KEY = "AIzaSyDsTiJy0TeYCkjiJqq6h9ukxOOvw9ASoFQ"
  var DISCOVERY_DOCS = ["https://www.googleapis.com/discovery/v1/apis/calendar/v3/rest"]
  var SCOPES = "https://www.googleapis.com/auth/calendar.events"

  window.onLoadCallback = function(){
    gapi.auth2.init({
        client_id: 'You_client_Id_HERE.apps.googleusercontent.com'
      });


gapi.client.init({
  apiKey: API_KEY,
  clientId: CLIENT_ID,
  discoveryDocs: DISCOVERY_DOCS,
  scope: SCOPES,
  ux_mode: 'redirect'
}).then(function Session() {

  const responseGoogle = response => {
    console.log(response)
    const { code } = response
    axios.post('/api/create-tokens', { code })
      .then(response => {
        console.log(response.data)
        setSignedIn(true)
      })
      .catch(error => console.log(error.message))
  }

  const handleSubmit = (e) => {
    e.preventDefault()

    // console.log(summary, description, location, startDateTime, endDatetime)
    axios.post('/api/create-event', {
      summary,
      description,
      location,
      startDateTime,
      endDatetime,
    })
    .then(response => {
      console.log(response.data)
      setSignedIn(true)
    })
    .catch(error => console.log(error.message))

  }


  const responseError = error => {
    console.log(error)
  }

  const [summary, setSummary] = useState('')
  const [description, setDescription] = useState('')
  const [location, setLocation] = useState('')
  const [startDateTime, setStartDateTime] = useState('')
  const [endDatetime, setEndDateTime] = useState('')
  const [signedIn, setSignedIn] = useState(false)

  return (
      <div>
        <div className="App">
          <h1>Add Sessions and Events</h1>
          </div>


                  <div>
                  <GoogleLogin 
                      clientId="708105023641-t350k3usoo751tfnilu9hrdokjmmli8v.apps.googleusercontent.com"
                      buttonText="Add sessions and events"
                      onSuccess={responseGoogle}
                      onFailure={responseError}
                      cookiePolicy={'single_host_origin'}

                      responseType="code"
                      accessType="offline"
                      scope="openid email profile https://www.googleapis.com/auth/calendar"
                      />
          </div>


      <div className='form'>
      <form onSubmit={handleSubmit}>
        <label htmlFor='summary'>Title</label>
        <br />
        <input type='text' id='summary' value={summary} onChange={e => setSummary(e.target.value) } />
      <br />

      <label htmlFor='description'>Description</label>
        <br />
        <textarea id='description' value={description} onChange={e => setDescription (e.target.value)} />
      <br />

      {/* <label htmlFor='location'>location</label>
        <br />
        <input type='text' id='locaion' value={location} onChange={e => setLocation (e.target.value)} />
      <br /> */}

      <label htmlFor='startDateTime'>Starting at</label>
        <br />
        <input type='datetime-local' id='startdate' value={startDateTime} onChange={e => setStartDateTime} />
      <br />

      <label htmlFor='endDateTime'>End date</label>
        <br />
        <input type='datetime-local' id='enddate' value={endDatetime} onChange={e => setEndDateTime} />
      <br />
      <button className='button-event' type='submit'>Create event</button>
      </form>
    </div>


</div>
)

});
  }

export default Session
