         <div class="card">
           <div class="card-body bg-dark">
             <h5 class="card-title">{announcement.message}</h5>
             <h6 class="card-subtitle mb-2 text-muted">{contact.message}</h6>
             {/* <p class="card-text">{contact.company.catchPhrase}</p> */}
